import React, { useState } from 'react';
import styled from 'styled-components';
import { useGame } from '../contexts/GameContext';

const Container = styled.div`
  background-color: rgba(30, 30, 46, 0.8);
  border-radius: 10px;
  padding: 15px;
  margin: 15px 0;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const Title = styled.h3`
  color: #e2b714;
  margin-top: 0;
  margin-bottom: 15px;
  text-align: center;
  font-size: 1.2rem;
`;

const TabContainer = styled.div`
  display: flex;
  margin-bottom: 15px;
  border-bottom: 1px solid #444;
`;

const Tab = styled.button`
  background: ${props => props.$active ? 'rgba(226, 183, 20, 0.2)' : 'transparent'};
  color: ${props => props.$active ? '#e2b714' : '#ccc'};
  border: none;
  padding: 8px 15px;
  cursor: pointer;
  font-weight: ${props => props.$active ? 'bold' : 'normal'};
  border-bottom: 2px solid ${props => props.$active ? '#e2b714' : 'transparent'};
  transition: all 0.3s;
  
  &:hover {
    background: rgba(226, 183, 20, 0.1);
  }
`;

const RequestForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-bottom: 20px;
`;

const Select = styled.select`
  padding: 8px;
  border-radius: 5px;
  background-color: #2c2c44;
  color: white;
  border: 1px solid #444;
`;

const InputGroup = styled.div`
  display: flex;
  gap: 10px;
`;

const Input = styled.input`
  padding: 8px;
  border-radius: 5px;
  background-color: #2c2c44;
  color: white;
  border: 1px solid #444;
  flex: 1;
`;

const Button = styled.button`
  padding: 8px 15px;
  border-radius: 5px;
  border: none;
  background-color: ${props => props.$color || '#4caf50'};
  color: white;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover:not(:disabled) {
    transform: translateY(-2px);
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const RequestList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const RequestCard = styled.div`
  background-color: rgba(44, 44, 68, 0.7);
  border-radius: 5px;
  padding: 12px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const RequestHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Username = styled.span`
  font-weight: bold;
  color: #e2b714;
`;

const Amount = styled.span`
  font-weight: bold;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  justify-content: flex-end;
`;

const EmptyMessage = styled.p`
  text-align: center;
  color: #aaa;
  font-style: italic;
`;

const MoneyRequests = () => {
  const { 
    socket,
    players, 
    pendingMoneyRequests, 
    sentMoneyRequests, 
    activeLoans,
    requestMoney,
    acceptMoneyRequest,
    rejectMoneyRequest,
    repayLoan
  } = useGame();
  
  const [activeTab, setActiveTab] = useState('request');
  const [selectedPlayer, setSelectedPlayer] = useState('');
  const [requestAmount, setRequestAmount] = useState('');
  
  // Filter out the current player from the list
  const otherPlayers = players.filter(player => player.id !== socket?.id);
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedPlayer && requestAmount) {
      requestMoney(selectedPlayer, requestAmount);
      setRequestAmount('');
    }
  };
  
  const renderRequestForm = () => (
    <RequestForm onSubmit={handleSubmit}>
      <Select 
        value={selectedPlayer} 
        onChange={(e) => setSelectedPlayer(e.target.value)}
        required
      >
        <option value="">Select a player</option>
        {otherPlayers.map(player => (
          <option key={player.id} value={player.id}>
            {player.username} (Balance: {player.balance})
          </option>
        ))}
      </Select>
      
      <InputGroup>
        <Input 
          type="number" 
          placeholder="Amount" 
          min="1"
          value={requestAmount} 
          onChange={(e) => setRequestAmount(e.target.value)}
          required
        />
        <Button type="submit" disabled={!selectedPlayer || !requestAmount}>
          Request
        </Button>
      </InputGroup>
    </RequestForm>
  );
  
  const renderPendingRequests = () => (
    <RequestList>
      {pendingMoneyRequests.length === 0 ? (
        <EmptyMessage>No pending requests</EmptyMessage>
      ) : (
        pendingMoneyRequests.map(request => (
          <RequestCard key={request.requestId}>
            <RequestHeader>
              <div>
                <Username>{request.requester.username}</Username> is requesting <Amount>{request.amount} chips</Amount>
              </div>
            </RequestHeader>
            <div>If accepted, they will owe you <Amount>{request.returnAmount} chips</Amount> (1.5x)</div>
            <ButtonGroup>
              <Button 
                $color="#f44336" 
                onClick={() => rejectMoneyRequest(request.requestId)}
              >
                Reject
              </Button>
              <Button 
                onClick={() => acceptMoneyRequest(request.requestId)}
              >
                Accept
              </Button>
            </ButtonGroup>
          </RequestCard>
        ))
      )}
    </RequestList>
  );
  
  const renderSentRequests = () => (
    <RequestList>
      {sentMoneyRequests.length === 0 ? (
        <EmptyMessage>No sent requests</EmptyMessage>
      ) : (
        sentMoneyRequests.map(request => (
          <RequestCard key={request.requestId}>
            <RequestHeader>
              <div>
                You requested <Amount>{request.amount} chips</Amount> from <Username>{request.targetPlayer.username}</Username>
              </div>
            </RequestHeader>
            <div>Waiting for response...</div>
          </RequestCard>
        ))
      )}
    </RequestList>
  );
  
  const renderActiveLoans = () => (
    <RequestList>
      {activeLoans.length === 0 ? (
        <EmptyMessage>No active loans</EmptyMessage>
      ) : (
        activeLoans.map(loan => (
          <RequestCard key={loan.requestId}>
            {loan.type === 'borrower' ? (
              <>
                <RequestHeader>
                  <div>
                    You borrowed <Amount>{loan.amount} chips</Amount> from <Username>{loan.lender.username}</Username>
                  </div>
                </RequestHeader>
                <div>You owe <Amount>{loan.returnAmount} chips</Amount> (1.5x)</div>
                <ButtonGroup>
                  <Button 
                    onClick={() => repayLoan(loan.requestId)}
                  >
                    Repay Loan
                  </Button>
                </ButtonGroup>
              </>
            ) : (
              <>
                <RequestHeader>
                  <div>
                    <Username>{loan.borrower.username}</Username> borrowed <Amount>{loan.amount} chips</Amount> from you
                  </div>
                </RequestHeader>
                <div>They owe you <Amount>{loan.returnAmount} chips</Amount> (1.5x)</div>
              </>
            )}
          </RequestCard>
        ))
      )}
    </RequestList>
  );
  
  return (
    <Container>
      <Title>Money Requests & Loans</Title>
      
      <TabContainer>
        <Tab 
          $active={activeTab === 'request'} 
          onClick={() => setActiveTab('request')}
        >
          Request Money
        </Tab>
        <Tab 
          $active={activeTab === 'pending'} 
          onClick={() => setActiveTab('pending')}
        >
          Pending ({pendingMoneyRequests.length})
        </Tab>
        <Tab 
          $active={activeTab === 'sent'} 
          onClick={() => setActiveTab('sent')}
        >
          Sent ({sentMoneyRequests.length})
        </Tab>
        <Tab 
          $active={activeTab === 'loans'} 
          onClick={() => setActiveTab('loans')}
        >
          Loans ({activeLoans.length})
        </Tab>
      </TabContainer>
      
      {activeTab === 'request' && renderRequestForm()}
      {activeTab === 'pending' && renderPendingRequests()}
      {activeTab === 'sent' && renderSentRequests()}
      {activeTab === 'loans' && renderActiveLoans()}
    </Container>
  );
};

export default MoneyRequests;
