import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useGame } from '../contexts/GameContext';

const ControlsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  max-width: 600px;
  margin: 0 auto;
  padding: 1rem;
  background-color: rgba(30, 30, 46, 0.8);
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
`;

const ActionsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 10px;
  width: 100%;
  margin-bottom: 1rem;
  
  @media (max-width: 600px) {
    grid-template-columns: repeat(2, 1fr);
  }
`;

const ActionButton = styled.button`
  padding: 0.8rem 0;
  border-radius: 5px;
  border: none;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  
  &:hover:not(:disabled) {
    transform: translateY(-3px);
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
  
  svg {
    margin-bottom: 5px;
    font-size: 1.2rem;
  }
`;

const HitButton = styled(ActionButton)`
  background-color: #4caf50;
  color: white;
`;

const StandButton = styled(ActionButton)`
  background-color: #f44336;
  color: white;
`;

const DoubleButton = styled(ActionButton)`
  background-color: #2196f3;
  color: white;
`;

const SplitButton = styled(ActionButton)`
  background-color: #ff9800;
  color: white;
`;

const SurrenderButton = styled(ActionButton)`
  background-color: #9e9e9e;
  color: white;
`;

const NewRoundButton = styled(ActionButton)`
  background-color: #9c27b0;
  color: white;
  grid-column: span 2;
`;

const Icon = styled.span`
  font-size: 1.5rem;
  margin-bottom: 5px;
`;

const ButtonText = styled.span`
  font-size: 0.9rem;
`;

const HintContainer = styled.div`
  background-color: rgba(226, 183, 20, 0.9);
  color: #000;
  padding: 10px 15px;
  border-radius: 5px;
  margin-bottom: 15px;
  font-weight: 600;
  text-align: center;
  animation: fadeIn 0.5s ease-in;
  
  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }
`;

const PlayerControls = ({ currentPlayer, canSplit }) => {
  const { 
    hit, 
    stand, 
    doubleDown, 
    split, 
    surrender, 
    startNewRound, 
    gameState, 
    isPlayerTurn,
    hintsEnabled
  } = useGame();
  
  const [showHint, setShowHint] = useState(false);
  const [hint, setHint] = useState('');
  
  // Timer for showing hints
  useEffect(() => {
    // Only show hints if enabled
    if (!hintsEnabled) {
      setShowHint(false);
      return;
    }
    
    const timer = setTimeout(() => {
      if (currentPlayer && isPlayerTurn()) {
        const hintText = getStrategyHint(currentPlayer);
        setHint(hintText);
        setShowHint(true);
      }
    }, 10000); // 10 seconds
    
    return () => clearTimeout(timer);
  }, [currentPlayer, isPlayerTurn, hintsEnabled]);
  
  // Reset hint when player makes a move
  const handleAction = (action) => {
    setShowHint(false);
    action();
  };
  
  // Get strategy hint based on player's hand and dealer's upcard
  const getStrategyHint = (player) => {
    if (!player || !player.cards || player.cards.length === 0) return '';
    
    // Get dealer's upcard value
    const dealerUpcard = getDealerUpcard();
    if (!dealerUpcard) return '';
    
    // Check if player has a pair
    const isPair = player.cards.length === 2 && 
                  getValue(player.cards[0]) === getValue(player.cards[1]);
    
    // Check if player has a soft hand (contains an Ace counted as 11)
    const hasSoftHand = player.cards.some(card => card.value === 'A') && 
                        player.score <= 21;
    
    if (isPair) {
      return getPairHint(getValue(player.cards[0]), dealerUpcard);
    } else if (hasSoftHand) {
      return getSoftTotalHint(player.score, dealerUpcard);
    } else {
      return getHardTotalHint(player.score, dealerUpcard);
    }
  };
  
  // Get dealer's upcard value
  const getDealerUpcard = () => {
    const { dealer } = useGame();
    if (!dealer || !dealer.cards || dealer.cards.length === 0) return null;
    
    const card = dealer.cards[0];
    return getValue(card);
  };
  
  // Convert card value to numeric value
  const getValue = (card) => {
    if (!card || !card.value) return 0;
    
    if (['J', 'Q', 'K'].includes(card.value)) return 10;
    if (card.value === 'A') return 11;
    return parseInt(card.value, 10);
  };
  
  // Get hint for pair hands
  const getPairHint = (pairValue, dealerUpcard) => {
    if (pairValue === 11) return 'Always split Aces.';
    if (pairValue === 10) return 'Never split Tens.';
    
    if (pairValue === 9) {
      if ([2, 3, 4, 5, 6, 8, 9].includes(dealerUpcard)) return 'Split 9s against dealer 2-6, 8-9.';
      return 'Stand with 9s against dealer 7, 10, or Ace.';
    }
    
    if (pairValue === 8) return 'Always split 8s.';
    
    if (pairValue === 7) {
      if (dealerUpcard >= 2 && dealerUpcard <= 7) return 'Split 7s against dealer 2-7.';
      return 'Hit with 7s against dealer 8-Ace.';
    }
    
    if (pairValue === 6) {
      if (dealerUpcard >= 2 && dealerUpcard <= 6) return 'Split 6s against dealer 2-6.';
      return 'Hit with 6s against dealer 7-Ace.';
    }
    
    if (pairValue === 5) {
      if (dealerUpcard >= 2 && dealerUpcard <= 9) return 'Double with 5s against dealer 2-9.';
      return 'Hit with 5s against dealer 10-Ace.';
    }
    
    if (pairValue === 4) {
      if (dealerUpcard === 5 || dealerUpcard === 6) return 'Split 4s against dealer 5-6.';
      return 'Hit with 4s against other dealer cards.';
    }
    
    if (pairValue === 3 || pairValue === 2) {
      if (dealerUpcard >= 2 && dealerUpcard <= 7) return `Split ${pairValue}s against dealer 2-7.`;
      return `Hit with ${pairValue}s against dealer 8-Ace.`;
    }
    
    return '';
  };
  
  // Get hint for soft totals
  const getSoftTotalHint = (total, dealerUpcard) => {
    if (total >= 20) return 'Stand with Soft 20.';
    
    if (total === 19) {
      if (dealerUpcard === 6) return 'Double with Soft 19 against dealer 6.';
      return 'Stand with Soft 19.';
    }
    
    if (total === 18) {
      if (dealerUpcard >= 2 && dealerUpcard <= 6) return 'Double with Soft 18 against dealer 2-6.';
      if (dealerUpcard >= 9 || dealerUpcard === 11) return 'Hit with Soft 18 against dealer 9-Ace.';
      return 'Stand with Soft 18 against dealer 7-8.';
    }
    
    if (total === 17) {
      if (dealerUpcard >= 3 && dealerUpcard <= 6) return 'Double with Soft 17 against dealer 3-6.';
      return 'Hit with Soft 17 against other dealer cards.';
    }
    
    if (total === 16) {
      if (dealerUpcard >= 4 && dealerUpcard <= 6) return 'Double with Soft 16 against dealer 4-6.';
      return 'Hit with Soft 16 against other dealer cards.';
    }
    
    if (total === 15) {
      if (dealerUpcard >= 4 && dealerUpcard <= 6) return 'Double with Soft 15 against dealer 4-6.';
      return 'Hit with Soft 15 against other dealer cards.';
    }
    
    if (total === 14) {
      if (dealerUpcard === 5 || dealerUpcard === 6) return 'Double with Soft 14 against dealer 5-6.';
      return 'Hit with Soft 14 against other dealer cards.';
    }
    
    if (total === 13) {
      if (dealerUpcard === 5 || dealerUpcard === 6) return 'Double with Soft 13 against dealer 5-6.';
      return 'Hit with Soft 13 against other dealer cards.';
    }
    
    return 'Hit with this soft total.';
  };
  
  // Get hint for hard totals
  const getHardTotalHint = (total, dealerUpcard) => {
    if (total >= 17) return 'Stand with 17 or higher.';
    
    if (total >= 13 && total <= 16) {
      if (dealerUpcard >= 2 && dealerUpcard <= 6) return `Stand with ${total} against dealer 2-6.`;
      return `Hit with ${total} against dealer 7-Ace.`;
    }
    
    if (total === 12) {
      if (dealerUpcard >= 4 && dealerUpcard <= 6) return 'Stand with 12 against dealer 4-6.';
      return 'Hit with 12 against dealer 2-3, 7-Ace.';
    }
    
    if (total === 11) return 'Always double with 11.';
    
    if (total === 10) {
      if (dealerUpcard >= 2 && dealerUpcard <= 9) return 'Double with 10 against dealer 2-9.';
      return 'Hit with 10 against dealer 10-Ace.';
    }
    
    if (total === 9) {
      if (dealerUpcard >= 3 && dealerUpcard <= 6) return 'Double with 9 against dealer 3-6.';
      return 'Hit with 9 against other dealer cards.';
    }
    
    return 'Hit with 8 or lower.';
  };
  
  // Check if it's the player's turn directly
  const playerTurn = isPlayerTurn();
  
  // Check if this is a split hand
  const isSplitHand = currentPlayer?.id?.includes('-split');
  
  const isFirstAction = currentPlayer?.cards?.length === 2;
  const isGameEnded = gameState === 'ended';
  
  // Check if player can double down (only with first 2 cards)
  const canDoubleDown = playerTurn && isFirstAction;
  
  // Check if player can surrender (only as first action)
  const canSurrender = playerTurn && isFirstAction;
  
  // Can't split a split hand
  const canSplitHand = canSplit && !isSplitHand;
  
  return (
    <ControlsContainer>
      {showHint && hint && (
        <HintContainer>
          <span role="img" aria-label="hint">üí°</span> {hint}
        </HintContainer>
      )}
      
      <ActionsGrid>
        {isGameEnded ? (
          <NewRoundButton onClick={startNewRound}>
            <Icon>üîÑ</Icon>
            <ButtonText>New Round</ButtonText>
          </NewRoundButton>
        ) : (
          <>
            <HitButton 
              onClick={() => handleAction(hit)} 
              disabled={!playerTurn}
              title="Draw another card"
            >
              <Icon>üëÜ</Icon>
              <ButtonText>Hit</ButtonText>
            </HitButton>
            
            <StandButton 
              onClick={() => handleAction(stand)} 
              disabled={!playerTurn}
              title="End your turn"
            >
              <Icon>‚úã</Icon>
              <ButtonText>Stand</ButtonText>
            </StandButton>
            
            <DoubleButton 
              onClick={() => handleAction(doubleDown)} 
              disabled={!playerTurn || !canDoubleDown}
              title="Double your bet and receive one more card"
            >
              <Icon>üí∞</Icon>
              <ButtonText>Double</ButtonText>
            </DoubleButton>
            
            <SplitButton 
              onClick={() => handleAction(split)} 
              disabled={!playerTurn || !canSplitHand}
              title="Split your pair into two hands"
            >
              <Icon>‚úÇÔ∏è</Icon>
              <ButtonText>Split</ButtonText>
            </SplitButton>
            
            <SurrenderButton 
              onClick={() => handleAction(surrender)} 
              disabled={!playerTurn || !canSurrender}
              title="Forfeit half your bet and end your hand"
            >
              <Icon>üè≥Ô∏è</Icon>
              <ButtonText>Surrender</ButtonText>
            </SurrenderButton>
          </>
        )}
      </ActionsGrid>
    </ControlsContainer>
  );
};

export default PlayerControls;
