import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { useGame } from '../context/GameContext';

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  max-width: 350px;
  height: 400px;
  background-color: rgba(30, 30, 46, 0.8);
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
`;

const ChatHeader = styled.div`
  padding: 12px;
  background-color: #272736;
  font-weight: 600;
  color: #e2b714;
  border-bottom: 1px solid #333;
  display: flex;
  align-items: center;
  
  svg {
    margin-right: 8px;
    font-size: 1.2rem;
  }
`;

const MessagesContainer = styled.div`
  flex: 1;
  padding: 12px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 8px;
  
  /* Scrollbar styling */
  &::-webkit-scrollbar {
    width: 8px;
  }
  
  &::-webkit-scrollbar-track {
    background: #1e1e2e;
  }
  
  &::-webkit-scrollbar-thumb {
    background: #333;
    border-radius: 4px;
  }
  
  &::-webkit-scrollbar-thumb:hover {
    background: #444;
  }
`;

const MessageBubble = styled.div`
  max-width: 80%;
  padding: 8px 12px;
  border-radius: 12px;
  font-size: 0.9rem;
  line-height: 1.4;
  word-break: break-word;
  align-self: ${props => props.isCurrentUser ? 'flex-end' : 'flex-start'};
  background-color: ${props => props.isCurrentUser ? '#2196f3' : '#333'};
  color: white;
  position: relative;
`;

const MessageSender = styled.div`
  font-size: 0.8rem;
  font-weight: 600;
  margin-bottom: 2px;
  color: ${props => props.isCurrentUser ? '#90caf9' : '#e2b714'};
`;

const MessageTime = styled.div`
  font-size: 0.7rem;
  color: rgba(255, 255, 255, 0.7);
  position: absolute;
  bottom: -18px;
  ${props => props.isCurrentUser ? 'right: 10px;' : 'left: 10px;'}
`;

const SystemMessage = styled.div`
  text-align: center;
  padding: 6px 12px;
  margin: 8px 0;
  font-size: 0.8rem;
  color: #aaa;
  background-color: rgba(0, 0, 0, 0.2);
  border-radius: 12px;
  font-style: italic;
`;

const ChatInputArea = styled.form`
  display: flex;
  padding: 12px;
  background-color: #272736;
  border-top: 1px solid #333;
`;

const ChatInput = styled.input`
  flex: 1;
  padding: 8px 12px;
  border-radius: 20px;
  border: none;
  background-color: #333;
  color: white;
  font-size: 0.9rem;
  
  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px rgba(33, 150, 243, 0.5);
  }
  
  &::placeholder {
    color: #aaa;
  }
`;

const SendButton = styled.button`
  margin-left: 8px;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #2196f3;
  color: white;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  transition: background-color 0.2s;
  
  &:hover {
    background-color: #1e88e5;
  }
  
  &:disabled {
    background-color: #666;
    cursor: not-allowed;
  }
`;

const formatTime = (timestamp) => {
  const date = new Date(timestamp);
  return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
};

// System message types
const SYSTEM_MESSAGES = {
  JOIN: 'joined the game',
  LEAVE: 'left the game',
  BET: 'placed a bet',
  WIN: 'won',
  LOSE: 'lost',
  PUSH: 'pushed (tied)',
  BLACKJACK: 'got blackjack!',
  BUST: 'busted',
  STAND: 'stands',
  SURRENDER: 'surrendered',
  DOUBLE: 'doubled down',
  SPLIT: 'split their hand',
  GAME_START: 'Game started',
  ROUND_START: 'New round started',
  DEALER_TURN: 'Dealer\'s turn',
  DEALER_BUST: 'Dealer busted!'
};

const Chat = () => {
  const { messages, sendMessage, user, socket } = useGame();
  const [messageText, setMessageText] = useState('');
  const messagesEndRef = useRef(null);
  
  // Scroll to bottom of messages when new ones arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);
  
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (messageText.trim()) {
      sendMessage(messageText);
      setMessageText('');
    }
  };
  
  return (
    <ChatContainer>
      <ChatHeader>
        <span>ðŸ’¬</span> Chat
      </ChatHeader>
      
      <MessagesContainer>
        {messages.map((msg, index) => (
          msg.type === 'system' ? (
            <SystemMessage key={index}>
              {msg.sender ? `${msg.sender} ${msg.content}` : msg.content}
            </SystemMessage>
          ) : (
            <MessageBubble 
              key={index} 
              isCurrentUser={user && msg.sender === user.username}
            >
              <MessageSender isCurrentUser={user && msg.sender === user.username}>
                {msg.sender}
              </MessageSender>
              {msg.content}
              <MessageTime isCurrentUser={user && msg.sender === user.username}>
                {formatTime(msg.timestamp)}
              </MessageTime>
            </MessageBubble>
          )
        ))}
        <div ref={messagesEndRef} />
      </MessagesContainer>
      
      <ChatInputArea onSubmit={handleSubmit}>
        <ChatInput 
          type="text" 
          placeholder="Type a message..." 
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          disabled={!socket || !user}
        />
        <SendButton type="submit" disabled={!messageText.trim() || !socket || !user}>
          <span>âž¤</span>
        </SendButton>
      </ChatInputArea>
    </ChatContainer>
  );
};

export default Chat;
