import React from 'react';
import styled from 'styled-components';
import Card from './Card';

const DealerContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  position: relative;
`;

const DealerTitle = styled.div`
  font-size: 1.2rem;
  font-weight: 600;
  padding: 6px 16px;
  margin-bottom: 15px;
  background-color: #333;
  color: white;
  border-radius: 20px;
  text-align: center;
`;

const RuleChip = styled.div`
  position: absolute;
  top: 50px;
  right: -50px;
  background-color: #1e1e2e;
  color: #aaa;
  padding: 4px 10px;
  border-radius: 20px;
  font-size: 0.9rem;
  font-style: italic;
  display: flex;
  align-items: center;
  
  &::before {
    content: 'ðŸ“';
    margin-right: 5px;
  }
`;

const DealerCardArea = styled.div`
  display: flex;
  justify-content: center;
  min-height: 130px;
  margin-bottom: 10px;
`;

const ScoreChip = styled.div`
  position: absolute;
  top: 50px;
  left: 55%;
  background-color: #2c2c44;
  color: ${props => props.$score > 21 ? '#f44336' : 'white'};
  border: 1px solid #444;
  border-radius: 50%;
  width: 30px;
  height: 30px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: bold;
  font-size: 0.9rem;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
`;

const DealerArea = ({ dealer, gameState, currentTurn }) => {
  const { cards, score } = dealer;
  
  // Only show the hole card when the game is ended or when it's the dealer's turn
  const showHoleCard = gameState === 'ended' || currentTurn === 'dealer';
  
  // This function likely handles player turns
  const handlePlayerTurn = (action) => {
    // Check if it's actually the player's turn
    if (currentTurn !== 'player') {
      return; // This might be preventing player actions
    }

    // Process the player's action (hit, stand, etc.)
    switch(action) {
      case 'hit':
        // Hit logic
        break;
      case 'stand':
        // Stand logic
        break;
      case 'double':
        // Double logic
        break;
      // ... other actions
    }
  };

  return (
    <DealerContainer>
      <DealerTitle>Dealer</DealerTitle>
      <RuleChip>Stands on 17</RuleChip>
      
      <DealerCardArea>
        {cards && cards.length > 0 ? (
          <>
            {/* First card (face up) */}
            <Card card={cards[0]} />
            
            {/* Second card (hole card) - hidden until dealer's turn or game ended */}
            {cards.length > 1 && (
              showHoleCard 
                ? <Card card={cards[1]} /> 
                : <Card hidden={true} />
            )}
            
            {/* Any additional cards */}
            {cards.length > 2 && cards.slice(2).map((card, index) => (
              <Card key={index + 2} card={card} />
            ))}
          </>
        ) : null}
      </DealerCardArea>
      
      {score > 0 && showHoleCard && (
        <ScoreChip $score={score}>{score}</ScoreChip>
      )}

      {/* The buttons might be disabled incorrectly */}
      <div className="player-actions">
        <button 
          onClick={() => handlePlayerTurn('hit')}
          disabled={currentTurn !== 'player'} // This condition might be wrong
        >
          Hit
        </button>
        <button 
          onClick={() => handlePlayerTurn('stand')}
          disabled={currentTurn !== 'player'} // This condition might be wrong
        >
          Stand
        </button>
        {/* ... other buttons */}
      </div>
    </DealerContainer>
  );
};

export default DealerArea;
